import React, { createContext, useContext, useEffect, useState } from "react";
import { socketService } from "../services";

const SocketContext = createContext();

export const SocketProvider = ({ children }) => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);

  // Giáº£ láº­p káº¿t ná»‘i socket
  useEffect(() => {
    console.log("ðŸ§© Mock Socket connected");
    socketService.connect();
    setConnected(true);

    // Giáº£ láº­p nháº­n tin nháº¯n tá»« server
    socketService.onMessage((msg) => {
      console.log("ðŸ“© Mock message received:", msg);
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      console.log("ðŸ§© Mock Socket disconnected");
      setConnected(false);
    };
  }, []);

  const sendMessage = (msg) => {
    console.log("âœ‰ï¸ Mock send message:", msg);
    socketService.sendMessage(msg);
    setMessages((prev) => [...prev, msg]);
  };

  const value = {
    connected,
    messages,
    sendMessage,
  };

  return (
    <SocketContext.Provider value={value}>
      {children}
    </SocketContext.Provider>
  );
};

// Hook dÃ¹ng trong ChatPage
export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error("useSocket must be used within SocketProvider");
  }
  return context;
};
