import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles';

const LoginPage = () => {
  const navigate = useNavigate();
  const { login, register, isAuthenticated, error, clearError } = useAuth();
  
  const [isLogin, setIsLogin] = useState(true); // true = login, false = register
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: '',
    confirmPassword: ''
  });
  const [localError, setLocalError] = useState('');
  const [loading, setLoading] = useState(false);

  // Redirect n·∫øu ƒë√£ ƒëƒÉng nh·∫≠p
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/chat');
    }
  }, [isAuthenticated, navigate]);

  // Clear error khi switch gi·ªØa login/register
  useEffect(() => {
    clearError();
    setLocalError('');
  }, [isLogin, clearError]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setLocalError('');
    clearError();
  };

  const validateForm = () => {
    if (!formData.email || !formData.password) {
      setLocalError('Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin');
      return false;
    }

    if (!isLogin) {
      if (!formData.username) {
        setLocalError('Vui l√≤ng nh·∫≠p t√™n ng∆∞·ªùi d√πng');
        return false;
      }
      if (formData.password !== formData.confirmPassword) {
        setLocalError('M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp');
        return false;
      }
      if (formData.password.length < 6) {
        setLocalError('M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±');
        return false;
      }
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);
    setLocalError('');

    try {
      let response;
      if (isLogin) {
        await login({
          email: formData.email,
          password: formData.password
        });
      } else {
        await register({
          email: formData.email,
          password: formData.password,
          username: formData.username
        });
      }
       if (response) {
      console.log("‚úÖ Mock login success, navigating to chat");
      navigate("/chat");   // üëà √©p chuy·ªÉn sang trang chat sau khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng
    }
      // Navigation s·∫Ω ƒë∆∞·ª£c x·ª≠ l√Ω b·ªüi useEffect khi isAuthenticated thay ƒë·ªïi
    } catch (err) {
      setLocalError(err.message || 'ƒê√£ x·∫£y ra l·ªói');
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setFormData({
      email: '',
      password: '',
      username: '',
      confirmPassword: ''
    });
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-header">
          <h1>üí¨ Chat App</h1>
          <p>{isLogin ? 'ƒêƒÉng nh·∫≠p ƒë·ªÉ b·∫Øt ƒë·∫ßu tr√≤ chuy·ªán' : 'T·∫°o t√†i kho·∫£n m·ªõi'}</p>
        </div>

        <form className="login-form" onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="username">T√™n ng∆∞·ªùi d√πng</label>
              <input
                type="text"
                id="username"
                name="username"
                value={formData.username}
                onChange={handleChange}
                placeholder="Nh·∫≠p t√™n c·ªßa b·∫°n"
                disabled={loading}
              />
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Nh·∫≠p email"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">M·∫≠t kh·∫©u</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
              disabled={loading}
            />
          </div>

          {!isLogin && (
            <div className="form-group">
              <label htmlFor="confirmPassword">X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u"
                disabled={loading}
              />
            </div>
          )}

          {(localError || error) && (
            <div className="error-message">
              {localError || error}
            </div>
          )}

          <button 
            type="submit" 
            className="submit-btn"
            disabled={loading}
          >
            {loading ? 'ƒêang x·ª≠ l√Ω...' : (isLogin ? 'ƒêƒÉng nh·∫≠p' : 'ƒêƒÉng k√Ω')}
          </button>
        </form>

        <div className="toggle-mode">
          <p>
            {isLogin ? 'Ch∆∞a c√≥ t√†i kho·∫£n?' : 'ƒê√£ c√≥ t√†i kho·∫£n?'}
            <button onClick={toggleMode} disabled={loading}>
              {isLogin ? 'ƒêƒÉng k√Ω ngay' : 'ƒêƒÉng nh·∫≠p'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
