import React, { useState, useRef, useEffect } from 'react';
import { useSocket, useTypingListener } from '../context/SocketContext';
import MessageItem from './MessageItem';
import '../styles/ChatWindow.css';

const ChatWindow = ({ conversation, messages, onSendMessage, currentUserId }) => {
  const { sendTyping, isUserOnline } = useSocket();
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState([]);
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showInfo, setShowInfo] = useState(false);
  const [filteredMessages, setFilteredMessages] = useState([]);

  // Auto scroll to bottom when new messages arrive
  useEffect(() => {
    scrollToBottom();
  }, [messages]);
  // Listen to typing events
  useTypingListener((data) => {
    if (conversation && data.conversationId === conversation._id) {
      if (data.isTyping) {
        setTypingUsers(prev => {
          if (!prev.includes(data.userId)) {
            return [...prev, data.userId];
          }
          return prev;
        });
      } else {
        setTypingUsers(prev => prev.filter(id => id !== data.userId));
      }
    }
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleInputChange = (e) => {
    setInputMessage(e.target.value);

    // Send typing indicator
    if (!isTyping && conversation) {
      setIsTyping(true);
      sendTyping(conversation._id, true);
    }

    // Clear previous timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // Set timeout to stop typing indicator
    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false);
      if (conversation) {
        sendTyping(conversation._id, false);
      }
    }, 2000);
  };
  const handleSearchToggle = () => {
  setShowSearch(!showSearch);
  if (showSearch) {
    setSearchQuery('');
    setFilteredMessages([]);
  }
};

const handleSearch = (e) => {
  const query = e.target.value;
  setSearchQuery(query);
  
  if (query.trim()) {
    const filtered = messages.filter(message => 
      message.content.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredMessages(filtered);
  } else {
    setFilteredMessages([]);
  }
};

const handleInfoToggle = () => {
  setShowInfo(!showInfo);
};
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!inputMessage.trim() || !conversation) return;

    onSendMessage(inputMessage.trim());
    setInputMessage('');
    
    // Stop typing indicator
    if (isTyping) {
      setIsTyping(false);
      sendTyping(conversation._id, false);
    }

    // Clear timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // TODO: Implement file upload
    console.log('File upload:', file);
    alert('Ch·ª©c nƒÉng upload file ƒëang ƒë∆∞·ª£c ph√°t tri·ªÉn');
  };

  const getConversationName = () => {
    if (!conversation) return '';
    if (conversation.isGroup) {
      return conversation.name;
    }
    return conversation.otherUser?.username || 'Unknown';
  };

  const getOnlineStatus = () => {
    if (!conversation) return '';
    if (conversation.isGroup) {
      const onlineCount = conversation.members?.filter(m => 
        isUserOnline(m._id)
      ).length || 0;
      return `${onlineCount} online`;
    }
    return isUserOnline(conversation.otherUser?._id) ? ' Online' : ' Offline';
  };

  if (!conversation) {
    return (
      <div className="chat-window empty">
        <div className="empty-state">
          <div className="empty-icon">üí¨</div>
          <h3>Ch·ªçn m·ªôt cu·ªôc tr√≤ chuy·ªán</h3>
          <p>Ch·ªçn t·ª´ danh s√°ch b√™n tr√°i ho·∫∑c t·∫°o cu·ªôc tr√≤ chuy·ªán m·ªõi</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-window">
      {/* Chat Header */}
          <div className="chat-window-header">
            <div className="header-info">
              <div className="conversation-avatar">
                {conversation.isGroup ? 'üë•' : (conversation.otherUser?.avatar || 'üë§')}
              </div>
              <div>
                <h3>{getConversationName()}</h3>
                <p className="online-status">{getOnlineStatus()}</p>
              </div>
            </div>
            <div className="header-actions">
      <button 
        className={`icon-btn ${showSearch ? 'active' : ''}`} 
        title="T√¨m ki·∫øm"
        onClick={handleSearchToggle}
      >
        üîç
      </button>
      <button 
        className={`icon-btn ${showInfo ? 'active' : ''}`} 
        title="Th√¥ng tin"
        onClick={handleInfoToggle}
      >
        ‚ÑπÔ∏è
      </button>
    </div>
      </div>

      {/* Messages Area */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>Ch∆∞a c√≥ tin nh·∫Øn n√†o</p>
            <p>H√£y b·∫Øt ƒë·∫ßu cu·ªôc tr√≤ chuy·ªán!</p>
          </div>
        ) : (
          messages.map((message) => (
            <MessageItem
              key={message._id}
              message={message}
              isOwn={message.senderId === currentUserId}
              showAvatar={conversation.isGroup}
            />
          ))
        )}
        
        {/* Typing Indicator */}
        {typingUsers.length > 0 && (
          <div className="typing-indicator">
            <div className="typing-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
            <span className="typing-text">ƒëang nh·∫≠p...</span>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="chat-input-container">
        <form onSubmit={handleSubmit} className="chat-input-form">
          <label htmlFor="file-upload" className="file-upload-btn">
            üìé
            <input
              id="file-upload"
              type="file"
              onChange={handleFileUpload}
              style={{ display: 'none' }}
            />
          </label>

          <input
            type="text"
            value={inputMessage}
            onChange={handleInputChange}
            placeholder="Nh·∫≠p tin nh·∫Øn..."
            className="message-input"
          />

          <button 
            type="submit" 
            className="send-btn"
            disabled={!inputMessage.trim()}
          >
            ‚û§
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatWindow;
